mapper.insert:

	SELECT 'MES'||MESSAGE_SEQ.NEXTVAL FROM DUAL

INSERT INTO message (
MES_CODE, EMP_NO, MES_CONTENT, MES_DATE, MES_SAVE, MES_DELETE
) VALUES (
#{mesCode}, #{empNo}, #{mesContent}, SYSDATE, 'N', 'N'
)

INSERT INTO mesrec (
MES_CODE, emp_no, mr_save, mr_delete
) VALUES (
#{mesCode}, #{empNo}, 'N', 'N'
)

mapper.update:
UPDATE MESREC
SET MR_SAVE = #{mrSave}
WHERE MES_CODE = #{mesCode}
