mapper.select:
/* kr.or.ddit.mapper.ScheduleMapper.list */ select SCH_CODE /* 일정 코드 */ , a.EMP_NO /* 일정 등록 사번 */ , SCH_TITLE /* 일정 제목 */ , SCH_CONTENT /* 일정 내용 */ , SCH_START /* 일정 시작 일시 */ , SCH_END /* 일정 종료 일시 */ , SCH_LOCATION /* 장소 */ , SCH_TYPE /* 일정 타입(개인,부서,회사 전체) */ , SCH_READ /* 일정 공개 여부 */ , SCH_COLOR /* 일정 색상 */ from schedule a, employee b where a.EMP_NO = #{empNo} and a.EMP_NO = b.EMP_NO and b.DEP_CODE = #{depCode}
union
select SCH_CODE /* 일정 코드 */ , a.EMP_NO /* 일정 등록 사번 */ , SCH_TITLE /* 일정 제목 */ , SCH_CONTENT /* 일정 내용 */ , SCH_START /* 일정 시작 일시 */ , SCH_END /* 일정 종료 일시 */ , SCH_LOCATION /* 장소 */ , SCH_TYPE /* 일정 타입(개인,부서,회사 전체) */ , SCH_READ /* 일정 공개 여부 */ , SCH_COLOR /* 일정 색상 */ from schedule a, employee b where a.SCH_TYPE = '회사일정'

		/* kr.or.ddit.mapper.ScheduleMapper.selectCar */ 
		select 
    		  b.AF_SAVE         /* 첨부파일 */  
		 from car a, attfile b
		where b.AF_TCODE = a.CAR_CODE
		  and a.CAR_CODE = #{carCode}

mapper.insert:

	select 'SCH'||sch_seq.nextval from dual


/* kr.or.ddit.mapper.ScheduleMapper.register */
insert into schedule
	(
	  SCH_CODE		/* 일정 코드 */
	, EMP_NO		/* 일정 등록 사번 */
	, SCH_TITLE		/* 일정 제목 */
	, SCH_CONTENT	/* 일정 내용 */
	, SCH_START		/* 일정 시작 일시 */
	, SCH_END		/* 일정 종료 일시 */
	, SCH_LOCATION	/* 장소 */
	, SCH_TYPE		/* 일정 타입(개인,부서,회사 전체) */
	, SCH_READ		/* 일정 공개 여부 */
	, SCH_COLOR		/* 일정 색상 */
	)
values
(
/* ('SCH'||sch_seq.nextval) */
  #{schCode}
, #{empNo}
, #{schTitle}
, #{schContent}
, #{schStart}
, #{schEnd}
, #{schLocation}
, #{schType}
, #{schRead}
, #{schColor}
)

/* kr.or.ddit.mapper.ScheduleMapper.registerMrRes */
 insert into MRRES
 	(
     	  sch_code   /* 일정코드 */
   		, mr_code    /* 회의실 코드 */
 	)
values
	(
    	 #{schCode}
  	   , #{mrCode}
	)

/* kr.or.ddit.mapper.ScheduleMapper.registerCarRes */
insert into CARRES
 (
      sch_code   /* 일정코드 */
   	, car_code   /* 차량 코드 */
 )
values
 (
	  #{schCode}
	, #{carCode}
 )

mapper.update:
/* kr.or.ddit.mapper.ScheduleMapper.updateMroom */
update MROOM
set
	MR_USE = 'N'	/* 회의실 사용여부 : N(예약)*/
where MR_CODE = #{mrCode}

/* kr.or.ddit.mapper.ScheduleMapper.updateCar */
update CAR
set
	CAR_USE = 'N'	/* 차량 사용여부 : N(예약)*/
where CAR_CODE = #{carCode}

/* kr.or.ddit.mapper.ScheduleMapper.updateCar */
update CAR
set
	CAR_USE = 'Y'	/* 차량 사용여부 : Y(예약가능)*/
where CAR_CODE = #{carCode}

mapper.delete:
/* kr.or.ddit.mapper.ScheduleMapper.register */
delete from schedule
where sch_code = #{schCode}
